PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE weapon (itemIndex INTEGER PRIMARY KEY, internalName TEXT, internalType TEXT, ubWeight INTEGER, ubPerPocket INTEGER, usPrice INTEGER, ubCoolness INTEGER, bReliability INTEGER, bRepairEase INTEGER, rateOfFire INTEGER, calibre TEXT, ubReadyTime INTEGER, ubShotsPer4Turns INTEGER, ubBulletSpeed INTEGER, ubImpact INTEGER, ubDeadliness INTEGER, ubMagSize INTEGER, usRange INTEGER, ubAttackVolume INTEGER, ubHitVolume INTEGER, attachment_Silencer BOOLEAN, attachment_LaserScope BOOLEAN, attachment_SpringAndBoltUpgrade BOOLEAN, attachment_GunBarrelExtender BOOLEAN, bDamageable BOOLEAN, bRepairable BOOLEAN, bWaterDamages BOOLEAN, bMetal BOOLEAN, bSinks BOOLEAN, bShowStatus BOOLEAN, standardReplacement TEXT, ubShotsPerBurst INTEGER, ubBurstPenalty INTEGER, bBigGunList BOOLEAN, bTwoHanded BOOLEAN, attachment_SniperScope BOOLEAN, attachment_UnderGLauncher BOOLEAN, bNotBuyable BOOLEAN, attachment_Bipod BOOLEAN, attachment_Duckbill BOOLEAN, sound TEXT, bNotEditor BOOLEAN, bDefaultUndroppable BOOLEAN, usSmokeEffect INTEGER, bElectronic BOOLEAN, shortName TEXT, name TEXT, description TEXT);
CREATE TABLE magazine (itemIndex INTEGER PRIMARY KEY, "internalName" TEXT, "calibre" TEXT, "capacity" INTEGER, "ammoType" TEXT, "ubWeight" INTEGER, "ubPerPocket" INTEGER, "usPrice" INTEGER, "ubCoolness" INTEGER, "bMetal" BOOLEAN, "bSinks" BOOLEAN, "standardReplacement" TEXT, "bBigGunList" BOOLEAN, "dontUseAsDefaultMagazine" BOOLEAN, "bNotBuyable" BOOLEAN);
CREATE VIEW magazines_fudged AS SELECT usPrice*ubPerPocket AS "$", ubPerPocket*ubWeight AS "kg",  ubPerPocket*capacity AS "rnd", * from magazine ORDER BY "$" desc;
COMMIT;
